const MCFormat={colors:{0:"#000000",1:"#0000AA",2:"#00AA00",3:"#00AAAA",4:"#AA0000",5:"#AA00AA",6:"#FFAA00",7:"#AAAAAA",8:"#555555",9:"#5555FF",a:"#55FF55",b:"#55FFFF",c:"#FF5555",d:"#FF55FF",e:"#FFFF55",f:"#ffffff"},formats:{l:"bold",m:"strike",n:"underline",o:"italic",r:"reset"},parse(s,t="ยง"){let e=[],o="";const r=s.split(new RegExp(`(${t}[0-9a-fklmnor])`,"i"));for(let s=0;s<r.length;s++){const i=r[s];if(i.startsWith(t)){const s=i.slice(1).toLowerCase();if(this.colors[s])e=e.filter((s=>!s.startsWith("color:"))),e.push(`color:${this.colors[s]}`);else if(this.formats[s]){const t=this.formats[s];if("reset"===t)e=[];else{const s=e.indexOf(t);-1===s?e.push(t):e.splice(s,1)}}}else{let s=i;e.forEach((t=>{"bold"===t?s=`<b>${s}</b>`:"strike"===t?s=`<strike>${s}</strike>`:"underline"===t?s=`<u>${s}</u>`:"italic"===t?s=`<i>${s}</i>`:t.startsWith("color:")&&(s=`<span style="${t};">${s}</span>`)})),o+=s}}return o}};